import { createApp } from "vue"; // Vue 3 için createApp importu
import App from "./App.vue";
import router from "./router"; // Oluşturduğumuz router'ı import ediyoruz
import '@fortawesome/fontawesome-free/css/all.css';
import '@fortawesome/fontawesome-free/js/all.js';

const app = createApp(App);

// Global event bus için
app.config.globalProperties.$eventBus = createApp({});

app.use(router) // Router'ı uygulamaya entegre ediyoruz
  .mount("#app");
