import { createRouter, createWebHistory } from "vue-router";
import BooksList from "../components/BooksList.vue";
import BookDetails from "../components/BookDetails.vue";
import AdminPanel from '../views/AdminPanel.vue';
import UserPanel from "../views/UserPanel.vue";
import HomePage from '../views/HomePage.vue'; // HomePage bileşeni dahil ediliyor
import LoginPage from "../views/LoginPage.vue";
import AdminLoginPage from "../views/AdminLoginPage.vue"; // Yönetici giriş sayfası


const routes = [
  { path: "/", name: "LoginPage", component: LoginPage },
  { path: '/home', name: 'HomePage', component: HomePage }, // Ana sayfa rotası
  {
    path: "/",
    name: "BooksList",
    component: BooksList,
  },
  { path: "/admin-login", name: "AdminLoginPage", component: AdminLoginPage },
  {
    path: "/book/:id",
    name: "BookDetails",
    component: BookDetails,
    props: true, // Dinamik parametreleri bileşene aktarmak için props'u etkinleştirin
  },
  
  { path: "/admin", name: "AdminPanel", component: AdminPanel },
  { path: "/user", name: "UserPanel", component: UserPanel }, // Kullanıcı paneli rotası
  
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

export default router;
