// Vue 3 için importlar
import { createRouter, createWebHistory } from "vue-router"; // Vue Router'ı bu şekilde import etmelisiniz
import BooksList from "@/components/BooksList.vue";
import BookDetail from "@/views/BookDetail.vue";

// Router'ı oluşturuyoruz
const router = createRouter({
  history: createWebHistory(), // Web History kullanıyoruz
  routes: [
    {
      path: "/",
      name: "BooksList",
      component: BooksList,
    },
    {
      path: "/book/:id",
      name: "BookDetail",
      component: BookDetail,
    },
  ],
});

export default router;
